"use strict";
require('dotenv').config();
var emoji = require('node-emoji');
var Client = require('discord.js').Client;
var client = new Client();
client.on('ready', function () {
    console.log(client.user.username + " has logged in.");
});
// Set the prefix
var prefix = '$';
var prefix2 = '#';
client.on('message', function (message) {
    // Exit and stop if the prefix is not there or if user is a bot
    if (message.author.bot)
        return;
    if (message.content.startsWith(prefix + 'ping')) {
        message.channel.send('pong!');
    }
    else if (message.content.startsWith(prefix + 'Hello there')) {
        message.channel.send('General Kenobi');
    }
    else if (message.content.startsWith(prefix + 'Well done bottie')) {
        message.channel.send('「(•⌒▽⌒•)ゞ');
    }
    else if (message.content.startsWith('(づ￣ ³￣)づ')) {
        message.channel.send('(づ￣ ³￣)づ');
    }
    else if (message.content.startsWith('Bye bottie')) {
        message.channel.send('(づ￣ ³￣)づ');
    }
    else if (message.content.startsWith(prefix2)) {
        message.channel.send(emoji.random().emoji);
    }
});
client.login(process.env.DISCORDJS_BOT_TOKEN);
